import React, {Component} from 'react'
import './App.css';
import data from './data'

class MainContent extends Component {
    constructor() {
        super()
        this.state = {
            i: 8
        }
    }

    render() {
        return(
            <div>
                <div>
                    <div className='fullName'>{`${data[this.state.i].name.first} ${data[this.state.i].name.last}`}</div>
                    <div className='personDescription'>
                        <p className='genericLabel'>From:</p>
                        <p>{`${data[this.state.i].name.first} ${data[this.state.i].name.last}`}</p>
                    </div>
                    <div>{data[this.state.i].city}</div>
                </div>
                <div>
                    <button></button>
                    <button></button>
                    <button></button>

                </div>
            </div>
        )
    }
}

export default MainContent
